package week1Day2Homework;

public class LearningArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int a=14;
			int b=3;
		
		    int add = a + b;
		    int sub = a - b;
	        int mul = a * b;
	        int divide = a / b;
	        int modulus =  a % b;
	        
	        System.out.println("Addition Of Two Number a and b = " + add);
	        System.out.println("Substraction Of Two Number a and b = "  + sub);
	        System.out.println("Multiplication Of Two Number a and b = "  + mul);
	        System.out.println("Division Of Two Number a and b = " + divide);
	        System.out.println("Remainder when divide a by b = " + modulus);
	}

}
