package week1Day2Homework;

public class LearningIncrementDecrementOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i= 4;
		System.out.println("Value of i =" + i);
		i++;
		System.out.println("Value of i++ = " + i);
		i--;
		System.out.println("Value of i-- =" + i);
		++i;
		System.out.println("Value of ++i = " + i);
		--i;
		System.out.println("Value of --i ="+ i);
	}

}
