package week1Day2Homework;

public class LearningRelationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int a = 1;
	        int b = 2;
	        if(a == b)
	            System.out.println("value of a == value of b");
	        if(a != b)
	            System.out.println("value of a != value of b");
	        if(a > b)
	            System.out.println("value of a > value of b");
	        if(a < b)
	            System.out.println("value of a < value of b");
	        if(a <= b)
	            System.out.println("value of a <= value of b");

	}

}
