package week1Day2Homework;

public class LearningTernaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 , num2;
		num1 = 10;
		num2 = (num1 == 1) ? 20: 30;
		System.out.println( "Value of num2 is : " +  num2 );
		num2 = (num1 == 10) ? 20: 30;
		System.out.println( "Value of num2 is : " + num2 );
	}

}
